<script>
   import { onMount } from 'svelte';
   import { fade } from 'svelte/transition';

   let showLoader = true;
   let cleanup = null;

   onMount(() => {
      const timeout = setTimeout(() => {
         showLoader = false;
      });

      cleanup = () => clearTimeout(timeout);
   });
</script>

